mapper.select:
select 

from signed
where sid = #{sid,jdbcType=INTEGER}

    select si.sid, stime, sbusinesstype, scustomername, scustomerschool, scustomercollege, scustomermajor, scustomercardid, scustomerbankcardid,
	 scustomergrade, speoplenum, studyfee, spacefee, backfee, depositfee, sale, dept, sarea, sremark,si.stateid,IFNULL(allmoney,0) `condition`,bank
	 from signed si LEFT JOIN (SELECT sid,sum(amount) allmoney FROM iaer WHERE sid=#{sid,jdbcType=INTEGER}) s ON s.sid = si.sid WHERE si.sid=#{sid,jdbcType=INTEGER}

mapper.update:
update drawback
set stateid = #{stateid,jdbcType=INTEGER}
where sid = #{sid,jdbcType=INTEGER} and dbid=#{dbid,jdbcType=INTEGER}

mapper.select:
   
   
 		SELECT id,time,type,amount,handler,remark,s.sid,s.sale,s.scustomername,s.dept scustomerschool
 	FROM signed s INNER JOIN iaer  i ON i.sid = s.sid
	WHERE scustomername LIKE #{scustomernames,jdbcType=VARCHAR} AND sale LIKE #{sale,jdbcType=VARCHAR}

mapper.delete:
 delete from signed
 where sid in
  
     #{ids}

mapper.update:
	UPDATE signed SET  stateid = 12
  where sid in
   
      #{ids}

mapper.select:
  	SELECT id,time,type,amount,handler,remark,s.sid,s.sale,s.scustomername,s.scustomerschool FROM iaer i
	INNER JOIN signed s ON s.sid=i.sid 
	
		 WHERE time BETWEEN #{stime,jdbcType=VARCHAR} AND #{sremark,jdbcType=VARCHAR}
	
	
		 AND scustomerschool= #{scustomerschool,jdbcType=VARCHAR}
	
	
		 AND type= #{sbusinesstype,jdbcType=VARCHAR}
	
	
		 AND remark= #{bank,jdbcType=VARCHAR}
	
	
		 AND sale= #{sale,jdbcType=VARCHAR}
	
	
		 AND handler= #{sarea,jdbcType=VARCHAR}

  	SELECT SUM(amount) amount,type FROM iaer
  	
		 WHERE time BETWEEN #{stime,jdbcType=VARCHAR} AND #{sremark,jdbcType=VARCHAR}
	
  	GROUP BY type ORDER BY amount DESC

SELECT SUM(s.studyfee+s.depositfee) spacefee ,sum(a) backfee,sale, COUNT(sale) speoplenum FROM signed s INNER JOIN (SELECT sid,SUM(amount) a,max(time) sksj from iaer WHERE remark in('学费','定金') AND type='收入' GROUP BY sid) i ON s.sid=i.sid where sksj BETWEEN #{stime,jdbcType=VARCHAR} AND #{sremark,jdbcType=VARCHAR} AND a>=s.studyfee+s.depositfee
AND sale like #{sale,jdbcType=VARCHAR}
GROUP BY sale

SELECT SUM(s.studyfee+s.depositfee) studyfee ,sum(a) depositfee,sale ,count(sale) speoplenum FROM signed s INNER JOIN ( SELECT sid,MAX(time), SUM( amount) a ,max(time) sksj FROM iaer WHERE time BETWEEN #{stime,jdbcType=VARCHAR} AND #{sremark,jdbcType=VARCHAR} AND type='支出' GROUP BY sid ) i ON s.sid = i.sid WHERE stateid=11
AND sale like #{sale,jdbcType=VARCHAR}
GROUP BY sale

SELECT  s.sid,stime,scustomername,studyfee,spacefee,backfee,depositfee,sale, sksj sremark,a bank  FROM signed s INNER JOIN (
	SELECT sid,MAX(time), SUM( amount) a ,max(time) sksj FROM iaer 
		WHERE time BETWEEN  #{stime,jdbcType=VARCHAR} AND #{sremark,jdbcType=VARCHAR} 
			AND type='支出' GROUP BY sid ) i
	ON s.sid = i.sid WHERE stateid=11 and sale like #{sale,jdbcType=VARCHAR}
